INSERT INTO metadata_items (id, library_section_id, parent_id, metadata_type, guid, media_item_count, title, title_sort, original_title, studio, rating, rating_count, tagline, summary, trivia, quotes, content_rating, content_rating_age, "index", absolute_index, duration, user_thumb_url, user_art_url, user_banner_url, user_music_url, user_fields, tags_genre, tags_collection, tags_director, tags_writer, tags_star, originally_available_at, available_at, expires_at, refreshed_at, year, added_at, created_at, updated_at, deleted_at, tags_country, extra_data, hash, audience_rating, changed_at, resources_changed_at) VALUES (266, 1, null, 1, 'com.plexapp.agents.imdb://tt1611823?lang=en', 1, 'Gallants', 'Gallants', 'Da lui toi', '', 6.09999990463257, null, 'Da lui toi (original title)', 'Produced under Andy Lau''s Focus Films with actor Lau Kar Tung on board as producer, directors Clement Cheng and Derek Kwok have a winner on their hands, as they exalt the spirit of what it means to never give up when the odds are stacked against you, and in some way live up that spirit of theirs when they had embarked to make this project which had clear risks. It will be extremely foolish to dismiss the film outright just because it has more elderly actors than youthful ones to draw the crowd, because as the adage goes, the oldest ginger is still the best, and this film is a testament to that. Look closely and you''ll see the sheer amount of veteran, legendary talent even, assembled who hail from the Shaw Brothers kung fu film era, with even the opening credits with the silhouette fights, and the way characters are introduced, paying certain homage to an era bygone.', null, null, '', null, 1, null, 5880000, 'metadata://posters/com.plexapp.agents.themoviedb_531930de4f491f0e505ff7286fc414feddad485d', 'metadata://art/com.plexapp.agents.themoviedb_88c92a5bf434fdd0fa1f3181de4c9b17ee0c30b2', '', '', '', 'Action|Comedy', '', 'Derek Kwok Chi-Kin|Clement Cheng Si-Kit', 'Derek Kwok Chi-Kin|Clement Cheng Si-Kit', 'Wong Yau-Nam|Chen Kuan-Tai|Bruce Leung Siu-Lung', '2010-06-04 01:00:00', null, null, '2015-07-19 02:04:18', 2010, '2012-09-03 14:43:51', '2015-01-04 15:43:46', '2015-07-19 02:04:18', null, 'Hong Kong', 'ma%3AchapterSource=', 'b7fd2a6ca2441c93c87c300291f8bd9b323c5f58', null, 10534, 10534);
